mapper.insert:
insert into SYS_USER (
) values (#{id,jdbcType=BIGINT}, #{code,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{telNo,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, #{status,jdbcType=TINYINT}, NOW(), NOW()
)

mapper.update:
update SYS_USER set
status = #{status,jdbcType=TINYINT},
modifyTime = Now() where code = #{code,jdbcType=VARCHAR}

mapper.delete:
delete from SYS_USER
where code in

        #{code}
